import React, { useEffect, useState } from "react";
import "./App.css";
import pixelmartHero from "./pixelmartHero.jpg"; // Import your image

const App = () => {
  const [scrollY, setScrollY] = useState(0);

  // Update scroll position on scroll
  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="app">
      {/* Header Section */}
      <header className="header">
        <div className="logo">PixelMart</div>
        <nav className="navbar">
          <ul>
            <li><a href="#features">Features</a></li>
            <li><a href="#vendors">For Vendors</a></li>
            <li><a href="#customers">For Customers</a></li>
            <li><a href="#contact">Contact Us</a></li>
          </ul>
        </nav>
        <div className="auth-buttons">
          <button className="btn login">Login</button>
          <button className="btn signup">Sign Up</button>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1 className="highlight">
            A Platform That Grows With You
          </h1>
          <p className="hero-description">
            Start your online store with ease, grow your business, and reach customers worldwide.
          </p>
          <div className="hero-buttons">
            <button className="btn explore">Explore Now</button>
            <button className="btn learn-more">Learn More</button>
          </div>
        </div>
        <div
          className="hero-image"
          style={{
            opacity: 1 - Math.min(scrollY / 500, 0.7), // Gradual transparency effect
          }}
        >
          <img src={pixelmartHero} alt="Hero" />
        </div>
      </section>

      {/* Other Sections */}
      {/* Code for other sections remains unchanged */}
    </div>
  );
};

export default App;
